import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { PasswordEncryptionService } from 'src/app/password-encryption.service';  // Ajusta la ruta según tu estructura de archivos

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  username: string = '';
  password: string = '';
  loginError: boolean = false;

  constructor(
    private router: Router,
    private passwordEncryptionService: PasswordEncryptionService
  ) {}

  async onSubmit() {
    const hashedPassword = await this.passwordEncryptionService.encryptPassword(this.password);

    if (this.username === 'martin' && await this.passwordEncryptionService.comparePassword('123', hashedPassword)) {
      this.router.navigate(['/todo-list']);
    } else {
      this.loginError = true;
    }
  }

  ngOnInit(): void {
  }
}
<h2>Login</h2>
<div class="container">
<form (submit)="onSubmit()">
    <div class="form-group">
        <label for="username">Usuario:</label>
        <input type="text" id="username" [(ngModel)]="username" name="username" class="form-control" required>
    </div>
    <div class="form-group">
        <label for="password">Contraseña:</label>
        <input type="password" id="password" [(ngModel)]="password" name="password" class="form-control" required>
    </div>
    <button type="submit" class="btn btn-primary">Iniciar Sesión</button>
</form>
</div>
